    public class MailService : IMailService
    {
        private readonly IConfiguration _configuration;

        public MailService(IConfiguration configuration)
                    {
            _configuration = configuration;
        }

        public void SendEmail(string email, string subject, string message)
        {
            MimeMessage mimeMessage = new MimeMessage();
            MailboxAddress from = new MailboxAddress(_configuration["MailService:Name"], _configuration["MailService:Email"]);
            MailboxAddress to = new MailboxAddress(email, email);

            mimeMessage.From.Add(from);
            mimeMessage.To.Add(to);

            var bodyBuilder = new BodyBuilder();
            bodyBuilder.HtmlBody = message;
            mimeMessage.Body = bodyBuilder.ToMessageBody();
            mimeMessage.Subject = subject;

            int port;
            int.TryParse(_configuration["MailService:Port"], out port);
            SmtpClient client = new SmtpClient();
            client.Connect(_configuration["MailService:Host"], port, false);
            client.Authenticate(_configuration["MailService:Email"], _configuration["MailService:Key"]);
            client.Send(mimeMessage);
            client.Disconnect(true);
        }

